mapper.select:
		SELECT 
        	st.state_id, st.state_name
        FROM
                states st
		 WHERE
        		st.record_status = 'A'


       	AND st.state_name iLIKE #{sSearch}




		      ORDER BY ${columnName}  ${sSortDir}


		      ORDER BY st.created_at DESC


        LIMIT ${iDisplayLength}  OFFSET  ${iDisplayStart}
